  def show
    @tweets = Tweet.parse_tweets("@" + params[:handle])["tweets"].map do |t|
      t.gsub("\n", " ")
    end

    @hits = Hash.new(0)
    @score = 0

    @tweets.each do |t|
      t.split(" ").each do |word|
        TOPIC_SCORE.each do |key, value|
          if value[:words].include?(word)
            @score += value[:score]
            @hits[key] += 1
          end
        end
      end
    end
    # render json: revenue_data.to_json
    @count = @tweets.count
    @payload = [{ name: params[:handle], score: @score }, { name: "Ellen", score: 199  }, { name: "time4depression", score: -66 }].to_json
